import React from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Sidebar from "./components/Sidebar";
import ModelHub from "./pages/ModelHub";
import Training from "./pages/Training";
import SqlTrainer from "./pages/SqlTrainer";
import Analysis from "./pages/Analysis";
import OCR from "./pages/OCR";
import Rag from "./pages/Rag";
import Translation from "./pages/Translation";
import { useAuth0 } from "@auth0/auth0-react";
import "./App.css";

export default function App() {
  const { isAuthenticated, isLoading, loginWithRedirect, logout, user } = useAuth0();
  const [menuOpen, setMenuOpen] = React.useState(true);

  React.useEffect(() => {
    if (typeof window !== "undefined" && window.innerWidth < 900) {
      setMenuOpen(false);
    }
  }, []);

  const layoutClass = `layout ${menuOpen ? "sidebar-open" : "sidebar-collapsed"}`;

  if (isLoading) return <div className="loading">Loading...</div>;

  return (
    <div className="app-container">
      {/* Header */}
      <header className="app-header">
        <div className="header-left">
          <button
            className="menu-toggle"
            onClick={() => setMenuOpen(!menuOpen)}
            aria-label="Toggle navigation"
            aria-expanded={menuOpen}
          >
            {menuOpen ? "☰" : "☰"}
          </button>
          <span className="brand">Imaginarium AI</span>
        </div>

        <div className="header-right">
          {isAuthenticated ? (
            <>
              <span style={{ marginRight: "10px" }}>{user?.email}</span>
              <button
                onClick={() =>
                  logout({ logoutParams: { returnTo: window.location.origin } })
                }
                className="auth-btn"
              >
                Logout
              </button>
            </>
          ) : (
            <button
              onClick={() => loginWithRedirect()}
              className="auth-btn"
            >
              Login
            </button>
          )}
        </div>
      </header>

      {/* Sidebar + Main content */}
      <div className={layoutClass}>
        <Sidebar menuOpen={menuOpen} setMenuOpen={setMenuOpen} />

        <main className="main-content">
          <Routes>
            {/* Default redirect */}
            <Route path="/" element={<Navigate to="/models" replace />} />

            {/* App pages */}
            <Route path="/models" element={<ModelHub />} />
            <Route path="/training" element={<Training />} />
            <Route path="/sql-trainer" element={<SqlTrainer />} />
            <Route path="/analysis" element={<Analysis />} />
            <Route path="/ocr" element={<OCR />} />
            <Route path="/rag" element={<Rag />} />
            <Route path="/translation" element={<Translation />} />

            {/* 404 fallback */}
            <Route path="*" element={<div>Page not found</div>} />
          </Routes>
        </main>
      </div>
    </div>
  );
}
